import PySimpleGUI as sg

play = b''
no_titleBar = False
fullscreen = False

sg.theme('DarkAmber')   # Add a touch of color
# All the stuff inside your window.
layout = [  [sg.Button(image_data=play, key='Ok', button_color=sg.theme_background_color(), border_width=0)],
            [sg.Button(image_data=play, key='Ok', button_color=sg.theme_background_color(), border_width=0)] ]

# Create the Window
window = sg.Window('Window Title', layout, finalize=True, no_titlebar=no_titleBar)
window.set_min_size([800, 480])
# Event Loop to process "events" and get the "values" of the inputs

if fullscreen:
    window.maximize()

while True:
    event, values = window.read()
    if event == sg.WIN_CLOSED or event == 'Cancel':  # if user closes window or clicks cancel
        break

    print(event, values)

window.close()
